        public static void PlayNotes(List<songNotes> songNotes)
        {
            foreach (songNotes songNote in songNotes)
            {
                if (songNote.note != 0)
                {
                    Console.Beep(songNote.note, songNote.duration);
                }
                else
                {
                    Thread.Sleep(songNote.duration);
                }
            }
        }

        public static int NoteToFreq(string note)
        {
            if (note == "C4")
                return 261;
            if (note == "C#" || note == "Db")
                return 277;
            if (note == "D")
                return 293;
            if (note == "D#" || note == "Eb")
                return 311;
            if (note == "E")
                return 329;
            if (note == "F")
                return 349;
            if (note == "F#" || note == "Gb")
                return 369;
            if (note == "G")
                return 392;
            if (note == "G#" || note == "Ab")
                return 415;
            if (note == "A")
                return 440;
            if (note == "A#" || note == "Bb")
                return 466;
            if (note == "B")
                return 493;
            if (note == "C5")
                return 523;
            if (note == "REST")
                return 0;
            else
                return -1;
        }

        public static int DurationToTime(string note)
        {
            if (note == "1/4")
                return 1600 / 4;
            if (note == "1/2")
                return 1600 / 2;
            if (note == "1")
                return 1600;
            if (note == "2")
                return 1600 * 2;
            if (note == "3")
                return 1600 * 3;
            if (note == "4")
                return 1600 * 4;
            else
                return 0;
        }

